from typing import List
from functools import lru_cache

class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        mod = 10**9 + 7

        @lru_cache(None)
        def count_pairs(index, prev_a, prev_b):
            if index == len(nums):
                return 1

            total_count = 0
            for a in range(nums[index] + 1):
                b = nums[index] - a
                if prev_a <= a and prev_b >= b:
                    total_count += count_pairs(index + 1, a, b)
                    total_count %= mod

            return total_count

        return count_pairs(0, -1, float('inf'))
