typedef long long int ll;

const ll mod = 1e9+7;

class Solution {
public:
    int countOfPairs(vector<int>& arr) {


        ll n = arr.size();

        ll val1[1001];
        ll nxt[1001];

        for( ll i=0; i<=1000; i++) {
            val1[i] = nxt[i] = 0;
        }

        for( ll i=0; i<=arr[0]; i++) {
            val1[i] = 1;
        } 

        for( ll ii=1; ii<n; ii++) {
            if(arr[ii]>=arr[ii-1]) {
                ll d = arr[ii]-arr[ii-1];
                ll tot = 0;
                for( ll i=0, j=d; i<=arr[ii-1]; i++, j++) {
                    tot = (tot+val1[i])%mod;
                    nxt[j] = tot;
                }
            } else {
                ll tot = 0;
                for( ll i=0; i<=arr[ii]; i++) {
                    tot = (tot+val1[i])%mod;
                    nxt[i] = tot;
                }
            }
            for( ll i=0; i<=max( arr[ii-1], arr[ii]); i++) {
                val1[i] = nxt[i];
                nxt[i] = 0;
            }
        }

        ll ans = 0;
        for( ll i=0; i<=1000; i++) {
            ans = (ans+val1[i])%mod;
        }

        return ans;
    }
};
