class Solution {
    int m=(int)1e9+7;
    int [][][] dp;
    public int countOfPairs(int[] nums) {
        dp=new int[nums.length][51][51];
        for(int i=0;i<nums.length;i++){
            for(int j=0;j<51;j++) Arrays.fill(dp[i][j],-1);
        }
        return f(nums,0,0,50);
    }
    int f(int [] nums,int i,int n1,int n2){
        if(i>=nums.length) return 1;
        if(dp[i][n1][n2]!=-1) return dp[i][n1][n2];
       long ans=0;
        for(int y=n1;y<=nums[i];y++){
            if(nums[i]-y<=n2 && nums[i]-y>=0){
                ans=(ans+f(nums,i+1,y,nums[i]-y))%m;
            }
        }
        return dp[i][n1][n2]=(int)(ans%m);
    }
}
