class Solution {
public:
    int mod = 1000000007;
    int dp[2001][52][52];

    int helper(int ind, int prev1, int prev2, vector<int>& nums) {
        if (ind >= nums.size()) return 1;

        if (dp[ind][prev1][prev2] != -1) return dp[ind][prev1][prev2];

        int ways = 0;

        for (int i = prev1; i <= nums[ind]; i++) {
            if ((nums[ind] - i) <= prev2) {
                ways = (ways + helper(ind + 1, i, nums[ind] - i, nums)) % mod;
            }
        }

        return dp[ind][prev1][prev2] = ways;
    }

    int countOfPairs(vector<int>& nums) {
        memset(dp, -1, sizeof(dp)); 
        return helper(0, 0, 51, nums); 
    }
};
