class Solution {
    int dp[2003][52][52];
    const int M = 1e9 + 7;
    int find(vector<int> &nums, int indx, int l1, int l2) {
        if(indx >= nums.size()) return 1;
        int &ret = dp[indx][l1][l2];
        if(ret > -1) return ret;
        ret = 0;
        for(int x = l1; x <= nums[indx]; x++) {
            int y = nums[indx] - x;
            if(y > l2) continue;
            ret = (ret + find(nums, indx + 1, x, y)) % M;
        }
        return ret;
    }
public:
    int countOfPairs(vector<int>& nums) {
        memset(dp, -1, sizeof(dp));
        return find(nums, 0, 0, 50);
    }
};
