#define ll long long int

class Solution {
public:
    int countOfPairs(vector<int>& inputValues) {
        int numberOfElements = inputValues.size();
        vector<vector<vector<ll>>> memoization(numberOfElements, vector<vector<ll>>(51, vector<ll>(51, -1)));

        function<ll(int, int, int)> computePairs = [&](int currentIndex, int firstValue, int secondValue) -> ll {
            if (currentIndex >= inputValues.size()) return 1;
            ll pairCount = 0;
            ll moduloValue = 1e9+7; 
            if (memoization[currentIndex][firstValue][secondValue] != -1) return memoization[currentIndex][firstValue][secondValue];

            for (int i = firstValue; i <= inputValues[currentIndex]; i++) {
                for (int j = secondValue; j >= 0; j--) {
                    if (i + j == inputValues[currentIndex]) {
                        pairCount += computePairs(currentIndex + 1, i, j);
                        pairCount = pairCount % moduloValue;
                    }
                }
            }
            return memoization[currentIndex][firstValue][secondValue] = pairCount;
        };

        return computePairs(0, 0, inputValues[0]);
    }
};
