class Solution {
public:
    long long int solve(vector<int>& nums, int c1, int c2, int idx, int n, vector<vector<vector<int>>>& dp) {
        if (idx == n) return 1;
        int mod = 1e9 + 7;

        if (dp[idx][c1][c2] != -1) return dp[idx][c1][c2];

        int ans = 0;

        for (int i = c1; i <= nums[idx]; i++) {
            for (int j = c2; j >= 0; j--) {
                int curr = i + j;

                if (curr != nums[idx]) continue;
                ans = ((ans % mod) + (solve(nums, i, j, idx + 1, n, dp)%mod)) % mod;
            }
        }

        return dp[idx][c1][c2] = ans;
    }

    int countOfPairs(vector<int>& nums) {
        int n = nums.size();

        vector<vector<vector<int>>> dp(n, vector<vector<int>>(51, vector<int>(51, -1)));

        return solve(nums, 0, nums[0], 0, n, dp);
    }
};
