class Solution {
public:
    int solve(int i,int arr1,vector<int> &nums,vector<vector<int>> &dp){
        int n=nums.size(),arr2,res=0;
        if(i==n){
            return dp[i][arr1]=1;
        }
        if(dp[i][arr1]!=-1){
            return dp[i][arr1];
        }
        arr2=nums[i-1]-arr1;
        for(int j=0; j<=nums[i]; j++){
            if(j>=arr1 && nums[i]-j<=arr2){
                res=(res+solve(i+1,j,nums,dp))%1000000007;
            }
        }
        return dp[i][arr1]=res;
    }
    int countOfPairs(vector<int>& nums) {
        int n=nums.size(),res=0;
        vector<vector<int>> dp(n+1,vector<int>(51,-1));
        for(int i=0; i<=nums[0]; i++){
            res=(res+solve(1,i,nums,dp))%1000000007;
        }
        return res;
    }
};
