class Solution {
public:
    vector<int> nums;
    int n;
    vector<vector<vector<int>>> dp;
    const int MOD = 1000000007;

    int solve(int i, int a, int b) {
        if (i == n) return 1;
        if (dp[i][a][b] != -1) return dp[i][a][b];

        long res = 0;
        for(int a_ = a; a_ <= nums[i]; a_++) {
            int b_ = nums[i] - a_;
            if (b_ >= 0 && b_ <= b) {
                res = (res + solve(i + 1, a_, b_)) % MOD;
            }
        }
        dp[i][a][b] = res;
        return res;
    }

    int countOfPairs(vector<int>& nums) {
        this->nums = nums;
        n = nums.size();
        int maxNum = *max_element(nums.begin(), nums.end());
        dp.resize(n, vector<vector<int>>(maxNum + 1, vector<int>(maxNum + 1, -1)));
        long res = 0;
        for(int i = 0; i <= nums[0]; i++) {
            int j = nums[0] - i;
            if (j >= 0) {
                res = (res + solve(1, i, j)) % MOD;
            }
        }

        return (int)res;
    }
};
