class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        MOD = 10**9 + 7

        @cache
        def dp(ind,n1):
            n2 = nums[ind] - n1
            if n2 < 0:
                return 0
            if ind == len(nums)-1:
                return 1
            res = 0
            for i in range(n1,nums[ind+1]+1):
                j = nums[ind+1]-i
                if j <= n2:
                    res += dp(ind+1,i)
            return res

        ans = 0
        for i in range(nums[0]+1):
            ans += dp(0,i)
        return ans%MOD
