class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        MOD = 10**9 + 7
        n = len(nums)
        max_num = max(nums)

        # dp[i][x] will store the number of valid pairs up to index i where arr1[i] = x
        dp = [[0] * (max_num + 1) for _ in range(n)]

        # Initialize for the first index
        for x in range(nums[0] + 1):
            dp[0][x] = 1

        # Fill dp array
        for i in range(1, n):
            for x in range(nums[i] + 1):
                dp[i][x] = 0
                for y in range(nums[i-1] + 1):
                    if y <= x and nums[i] - x <= nums[i-1] - y:
                        dp[i][x] = (dp[i][x] + dp[i-1][y]) % MOD

        # Sum all valid sequences for the last index
        result = sum(dp[n-1]) % MOD

        return result
