class Solution:
    def countOfPairs(self, nums: List[int]) -> int:


        ans = 0
        MOD = 10 ** 9 + 7
        n = len(nums)
        @cache
        def f(idx,prev1,prev2):
            if idx == n:
                return 1
            ans = 0
            for i in range(nums[idx] + 1):

                j = nums[idx] - i
                if (prev1 is None and prev2 is None) or (prev1 <= i and prev2 >= j):
                    ans += f(idx + 1,i,j)
            return ans % MOD
        return f(0,None,None)
