class Solution {
public:
    int mod;
    int dp[2000][51];
    int f(int i,int prev,vector<int> &nums){
        if(i==nums.size()){
            return 1;
        }
        if(dp[i][prev]!=-1){
            return dp[i][prev];
        }
          int k=nums[i-1]-prev;
          long long int sum=0;
          for(int j=prev;j<=nums[i];j++){
              if(nums[i]-j<=k){
                  sum+=f(i+1,j,nums);
                  sum%=mod;
              }
          }
          return dp[i][prev]=sum;
    }
    int countOfPairs(vector<int>& nums) {
        mod=1e9+7;
        memset(dp,-1,sizeof(dp));
        long long int ans=0;
        for(int i=0;i<=nums[0];i++){
            ans+=f(1,i,nums);
            ans%=mod;
        }
        return ans;
    }
};
