class Solution {
public:
    int MOD = 1e9 + 7;

    int countOfPairs(vector<int>& nums) 
    {
        vector<vector<vector<int>>> dp(nums.size(), vector<vector<int>>(51, vector<int>(51, -1)));
        return recursor(nums, 0, 0, 50, dp);
    }

    int recursor(vector<int>& nums, int index, int lastOne, int lastSecond, vector<vector<vector<int>>>& dp)
    {
        if(index == nums.size())
        {
            return 1;
        }

        //cout << index << "  " << lastOne << "  " << lastSecond << endl;

        if(dp[index][lastOne][lastSecond] != -1)
        {
            return dp[index][lastOne][lastSecond];
        }

        long long sols = 0;

        for(int pairOne = lastOne; pairOne <= nums[index]; pairOne++)
        {
            int pairTwo = nums[index] - pairOne;

            if(pairTwo < 0 || pairTwo > lastSecond)
            {
                continue;
            }

            sols = (sols + recursor(nums, index + 1, pairOne, pairTwo, dp)) % MOD;
        }

        return dp[index][lastOne][lastSecond] = sols;
    }
};
