class Solution
{
public:
    int solve(int ind, vector<int> &nums, int prev1, int prev2,int& mod,vector<vector<vector<int>>>&dp){
        if(ind==nums.size()) return 1;
        if(dp[ind][prev1][prev2]!=-1)return dp[ind][prev1][prev2];
        long ans=0;
        for(int i=prev1;i<=nums[ind];i++){
            int rem=nums[ind]-i;
            if(rem<=prev2){
                ans=(ans+solve(ind+1,nums,i,rem,mod,dp))%mod;
            }
            // else return 0;
        }
        return dp[ind][prev1][prev2]=ans;
    }
    int countOfPairs(vector<int> &nums)
    {
        int mod=1e9+7;
        int prev1=0,prev2=50;
        int n=nums.size();
        vector<vector<vector<int>>>dp(n,vector<vector<int>>(51,vector<int>(51,-1)));
        return solve(0,nums,prev1,prev2,mod,dp);
    }
};
