class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        if len(nums) == 0:
            return 0
        M = 10**9+7
        @cache
        def dp(pos, last_digit, last_com_digit):
            if pos == len(nums):
                return 1
            ans = 0
            for i in range(last_digit, nums[pos]+1):
                digit = i
                com_digit = nums[pos] - digit
                if com_digit > last_com_digit:
                    continue
                ans = (ans + dp(pos+1, digit, com_digit))%M
            return ans
        return dp(0, 0, nums[0])
