public class Solution {
    private const long MOD = 1_000_000_007;

    public int CountOfPairs(int[] values) {
        int size = values.Length;
        long[,,] rkkk = new long[size, 51, 51];

        for (int i = 0; i < size; i++) {
            for (int j = 0; j <= 50; j++) {
                for (int k = 0; k <= 50; k++) {
                    rkkk[i, j, k] = -1;
                }
            }
        }

        long Solve(int index, int val1, int val2) {
            if (index >= values.Length) return 1;
            if (rkkk[index, val1, val2] != -1) return rkkk[index, val1, val2];

            long vishal = 0;

            for (int i = val1; i <= values[index]; i++) {
                for (int j = val2; j >= 0; j--) {
                    if (i + j == values[index]) {
                        vishal += Solve(index + 1, i, j);
                        vishal %= MOD;
                    }
                }
            }

            rkkk[index, val1, val2] = vishal;
            return vishal;
        }

        return (int)Solve(0, 0, values[0]);
    }
}
