class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        n = len(nums)
        md = 10**9 + 7
        dp = {}
        def helper(i, p1, p2):
            if i == n:
                return 1
            if (i, p1, p2) in dp:
                return dp[(i, p1, p2)]
            ans = 0
            st = max(0, p1)
            end = nums[i] + 1
            for opt in range(max(p1, 0), end):
                np1 = opt
                np2 = nums[i] - np1

                sub = 0
                if np1 >= p1 and np2 <= p2:
                    sub = helper(i + 1, np1, np2)

                    ans += sub
                    ans %= md
            dp[(i, p1, p2)] = ans
            return ans
        return helper(0, -1, float('inf'))
