class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        int n= nums.size();
        const int mod = 1e9+7;
        vector<vector<int>> dp(2, vector<int>(55,0));
        for(int j = 0; j <= nums[0]; j++)
        {
            dp[0][j] = 1;
        }

        for(int i = 1; i < n; i++)
        {
            int now = i%2;
            int prev = (i-1)%2;
            int x = nums[i];
            int y = nums[i-1];
            for(int j = 0; j < 55; j++)
            {
                dp[now][j] = 0;
            }
            for(int j = 0; j <= x; j++)
            {
                int jj = x-j;
                if(jj < 0)
                {
                    break;
                }
                for(int k = 0; k <= min(j,y-x+j); k++)
                {
                    dp[now][j] += dp[prev][k];
                    dp[now][j] %= mod;
                }
            }
        }

        int ans = 0;
        for(int j = 0; j < 55; j++)
        {
            ans += dp[(n+1)%2][j];
            ans %= mod;
        }

        return ans;
    }
};
