class Solution {
public:
    long long mod = 1e9+7;
    long long dp[2001][52][52];

    long long f(int i, int lasta, int lastb, vector<int>& nums) {
        if (i >= nums.size()) return 1;
        if (dp[i][lasta][lastb] != -1) return dp[i][lasta][lastb];
        long long ans = 0;
        for (int j = 0; j <= nums[i]; j++) {
            int a = j;
            int b = nums[i] - j;
            if (a >= lasta && b <= lastb) {
                ans = (ans + f(i + 1, a, b, nums) % mod) % mod;
            }
        }
        return dp[i][lasta][lastb] = ans;
    }

    long long countOfPairs(vector<int>& nums) {
        memset(dp, -1, sizeof(dp));
        return f(0, 0, 50, nums); 
    }
};
