#define ll long long

class Solution {
public:
    ll mod=1e9+7;
    ll dp[2001][1001];

    ll count(ll i,ll last,vector<int>& nums) {
        if(i==nums.size()) return 1;
        if (dp[i][last]!=-1) return dp[i][last];

        ll fk=0;
        for (ll j=last;j<=nums[i];j++) {
            ll tk=nums[i]-j;
            if(i==0||tk<=nums[i-1]-last) fk=(fk+count(i+1,j,nums))%mod;
        }
        return dp[i][last]=fk;
    }

    int countOfPairs(vector<int>& nums) {
        ll n=nums.size();
        ll maxi=0;
        for(auto p:nums) maxi=max(maxi,(ll)p);
        for(ll i=0;i<=n;i++) for(ll j=0;j<=maxi;j++) dp[i][j]=-1;
        memset(dp,-1,sizeof(dp));
        return count(0,0,nums);
    }
};
