class Solution {
public:
    int MOD = 1e9+7;
    int dp[2001][51];
    int helper(vector<int>& nums, int idx, int prev) {
        if (idx >= nums.size()) {
            return 1;
        }

        if (dp[idx][prev] != -1) {
            return dp[idx][prev];
        }

        int ans = 0;
        for (int i=prev; i<=nums[idx]; i++) {
            if (idx == 0 || nums[idx - 1] - prev >= nums[idx] - i) {
                ans += helper(nums, idx + 1, i);
                ans %= MOD;
            }
        }
        return dp[idx][prev] = ans;
    }

    int countOfPairs(vector<int>& nums) {
        memset(dp, -1, sizeof(dp));
        return helper(nums, 0, 0);
    }
};
