#define ll long long
#define mod 1000000007
class Solution {
   public:
    vector<ll> a;
    ll n, dp[2001][51][51];

    ll solve(ll pos, ll p1, ll p2) {
        if (pos >= n) return 1;
        if (dp[pos][p1][p2] != -1) return dp[pos][p1][p2];
        ll ans = 0;

        ll first = max(a[pos] - p1 + 1, 0ll);
        ll second = min(p2, a[pos]);
        if (first > second) {
            for (int i = p1; i <= a[pos]; i++) {
                int baki = a[pos] - i;
                if (baki > p2) continue;
                ans += solve(pos + 1, i, baki);
                ans %= mod;
            }
        } else {
            for(int i = p2; i >= 0; i--) {
                int baki = a[pos] - i;
                if(baki < p1) continue;
                ans += solve(pos + 1, baki, i);
                ans %= mod;
            }
        }
        return dp[pos][p1][p2] = ans;
    }
    int countOfPairs(vector<int>& nums) {
        n = nums.size();
        for(auto x : nums) a.push_back(x);
        memset(dp, -1, sizeof dp);
        int ans = solve(0, 0, 50);
        return ans;
    }
};
