class Solution {
public:
    int mod=1e9+7;
    int func(int last,int i,vector<int> &nums,vector<vector<int>> &dp){
        if(i==nums.size()){
            return 1;
        }
        if(last>nums[i]){
            return 0;
        }
        if(dp[last][i]!=-1){
            return dp[last][i];
        }
        int ans=0;
        if(i==0){
            for(int k=0;k<=nums[i];k++){
                ans=(ans+func(k,i+1,nums,dp)%mod)%mod;
            }
        }
        else{
            for(int k=last;k<=nums[i];k++){
                if(i>0 && (nums[i]-k)<=(nums[i-1]-last)){
                    ans=(ans+func(k,i+1,nums,dp)%mod)%mod;
                }
            }
        }
        return dp[last][i]=ans;
    }
    int countOfPairs(vector<int>& nums) {
        int n=nums.size();
        vector<vector<int>> dp(51,vector<int>(n+1,-1));
        return func(0,0,nums,dp);
    }
};
