class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        int mod = 1'000'000'007;
        int n = nums.size();
        vector<vector<int>> dp1(51, vector<int>(51, 0));
        vector<vector<int>> dp2(51, vector<int>(51));
        for (int x1 = 0; x1 <= nums[0]; x1++) {
            dp1[x1][nums[0]-x1] = 1;
        }
        for (int i = 1; i < n; i++) {
            for (auto&& row : dp2) {
                ranges::fill(row, 0);
            }
            for (int x1 = 0; x1 <= nums[i]; x1++) {
                int y1 = nums[i] - x1;
                for (int x0 = 0; x0 <= x1; x0++) {
                    int y0 = nums[i-1] - x0;
                    if (y0 < y1) break;
                    dp2[x1][y1] = (dp2[x1][y1] + dp1[x0][y0]) % mod;
                }
            }
            swap(dp1, dp2);
        }
        int count = 0;
        for (int i = 0; i < 51; i++)
            for (int j = 0; j < 51; j++)
                count = (count + dp1[i][j]) % mod;

        return count;
    }
};
