constexpr int MOD = 1'000'000'007;

int Add(int x, int y) {
    int s = x + y;
    if (s >= MOD) {
        s -= MOD;
    }
    return s;
}

class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        const int n = nums.size();
        const int mx = *max_element(nums.begin(), nums.end());
        reverse(nums.begin(), nums.end());

        vector<int> f(mx+1);
        for (int i = 0; i <= nums[0]; ++i) {
            f[i] = 1;
        }

        for (int i = 1; i < n; ++i) {
            vector<int> g(mx+1);
            for (int j = 0; j <= nums[i]; ++j) {
                int sj = nums[i] - j;
                int cur = 0;
                for (int k = 0; k <= j; ++k) {
                    int sk = nums[i-1] - k;
                    if (sk >= sj) {
                        cur = Add(cur, f[k]);
                    }
                }
                g[j] = cur;
            }
            f.swap(g);
        }

        int ans = 0;
        for (int i = 0; i <= mx; ++i) {
            ans = Add(ans, f[i]);
        }
        return ans;
    }
};
