class Solution {
public:
    int MOD = 1000000007;
    int findWays(int i, int lb, int ub, vector<int>& nums, vector<vector<vector<int>>> &dp) {
        if(i == nums.size()) return 1;
        if(dp[i][lb][ub] != -1) return dp[i][lb][ub];
        int num = nums[i], ways = 0;
        for(int k = 0; k <= num; k++) {
            int a  = k;
            if(a < lb) continue;
            int b = num - a;
            if(b > ub) continue;
            ways = ((ways%MOD) + (findWays(i+1, a, b, nums, dp)%MOD))%MOD;
        }
        return dp[i][lb][ub] = ways;
    }
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        vector<vector<vector<int>>> dp(n, vector<vector<int>>(55, vector<int>(55, -1)));
        return findWays(0, 0, 51, nums, dp);
    }
};
