from typing import List
from functools import lru_cache

class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        mod = 10**9 + 7

        @lru_cache(None)
        def backtrack(i, a_last, b_last):
            if i == len(nums):
                return 1

            count = 0
            for a in range(nums[i] + 1):
                b = nums[i] - a
                if a_last <= a and b_last >= b:
                    count += backtrack(i + 1, a, b)
                    count %= mod

            return count

        return backtrack(0, -1, float('inf'))
