class Solution {
public:
    int dp[2005][52][52];
    int mod=1e9+7;
    int fn(int prevx,int prevy,int i,vector<int> &nums){
        if(i>=nums.size()) return 1;
        int ans=0;
        if(dp[i][prevx+1][prevy+1]!=-1) return dp[i][prevx+1][prevy+1];
        if(prevx==-1 and prevy==-1){
            for(int j=0;j<=nums[i];j++){
                ans=(ans+fn(j,nums[i]-j,i+1,nums))%mod;
            }
        }
        else{
            for(int j=0;j<=nums[i];j++){
                if(j>=prevx and prevy>=nums[i]-j) ans=(ans+fn(j,nums[i]-j,i+1,nums))%mod;
            }
        }
        return dp[i][prevx+1][prevy+1]=ans;
    }
    int countOfPairs(vector<int>& nums) {
        int n=nums.size();
        memset(dp,-1,sizeof(dp));
        int ans=fn(-1,-1,0,nums);
        return ans;
    }
};
