class Solution {
public: 
    int n;
    int dp[2001][51][51];
    int dfs(int level, int prev_a, int prev_b, vector<int>& nums,int& mod) {
        if (level == n) return 1; 
        if(dp[level][prev_a][prev_b]!=-1) return dp[level][prev_a][prev_b];
        long long ans = 0;
        for (int i = prev_a; i <= nums[level]; i++) {
            int k = nums[level] - i;
            if (prev_b >= k) {
                ans += dfs(level + 1, i, k, nums,mod);
                ans=ans%mod;
            }
        }
        return dp[level][prev_a][prev_b]=(int)ans;
    }

    int countOfPairs(vector<int>& nums) {
        n = nums.size();
        int mod=1e9+7;
        memset(dp, -1, sizeof(dp));
        return dfs(0, 0, 50, nums,mod);
    }
};
