class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        long long prime =(1e9 + 7);
        int n=nums.size();
        long long dp[n+1][51][51];//ind,arr1,arr2
        memset(dp,0,sizeof(dp));
        for(int i=0;i<=50;i++)
            for(int j=0;j<=50;j++)
                dp[0][i][j]=1;
        for(int i=1;i<=n;i++)
        {
            int t=nums[i-1];
            for(int s=0;s<=t;s++)
            {
                (dp[i][s][t-s]+=dp[i-1][s][t-s])%=prime;
            }
            for(int j=1;j<=50;j++)
            {
                for(int k=0;k<=50;k++)
                {
                    (dp[i][j][k]+=dp[i][j-1][k])%=prime;
                }
            }
            for(int j=0;j<=50;j++)
            {
                for(int k=49;k>=0;k--)
                {
                    (dp[i][j][k]+=dp[i][j][k+1])%=prime;
                }
            }
        }
        // for(int i=0;i<=n;i++)
        // {
        //     cout<<i<<endl;
        //     for(int j=0;j<=5;j++)
        //     {
        //         for(int k=0;k<=5;k++)
        //         {
        //             cout<<dp[i][j][k]<<" ";
        //         }
        //         cout<<endl;
        //     }
        //     cout<<endl;
        // }
        long long ans=dp[n][nums[n-1]][0];
        return ans;
    }
};
