class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        MOD = 10**9 + 7
        n = len(nums)

        # Initialize a 2D dp array for the first element
        dp = [[0] * 51 for _ in range(51)]

        # Initial dp for the first element
        for x in range(nums[0] + 1):
            y = nums[0] - x
            dp[x][y] = 1

        # Process the rest of the array
        for i in range(1, n):
            new_dp = [[0] * 51 for _ in range(51)]
            for x in range(nums[i] + 1):
                y = nums[i] - x
                for prev_x in range(x + 1):
                    prev_y = nums[i-1] - prev_x
                    if prev_y >= y:
                        new_dp[x][y] = (new_dp[x][y] + dp[prev_x][prev_y]) % MOD
            dp = new_dp

        # Sum up all valid dp[n-1][x][y]
        result = 0
        for x in range(51):
            y = nums[-1] - x
            if y >= 0:
                result = (result + dp[x][y]) % MOD

        return result
