from functools import cache
class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        MOD = 10 ** 9 + 7
        cummin = []
        for n in reversed(nums):
            if not cummin:
                cummin.append(n)
            else:
                cummin.append(min(cummin[-1], n))
        cummin = list(reversed(cummin))
        # print(cummin)
        @cache
        def dp(start, lim, prevtake):
            # print(start, lim, prevtake)
            if start >= len(nums):
                return 1
            curr = nums[start]
            must_take = curr - lim
            ans = 0
            for i in range(max(must_take, 0, prevtake), cummin[start] + 1):
                ans += dp(start + 1, curr - i, i) % MOD
                ans %= MOD
            return ans % MOD
        return dp(0, float('inf'), 0)
