int f[2011][2011];
bool g[2011][2011];

const int mod = 1e9 + 7;
class Solution {
private:
    int n;
    vector<int> num;
public:
    int dp(int pos, int p1) {
        if (pos >= n)
            return 0;
        if (p1 > num[pos])
            return 0;
        if (g[pos][p1])
            return f[pos][p1];
        int ans = (pos == n - 1);
        (ans += dp(pos, p1 + 1)) %= mod;
        int p2 = num[pos] - p1;
        if (pos < n - 1)
            (ans += dp(pos + 1, max(p1, num[pos + 1] - p2))) %= mod;
        g[pos][p1] = true;
        return f[pos][p1] = ans;
    }

    int countOfPairs(vector<int>& nums) {
        n = nums.size();
        num = nums;
        for (int i = 0; i <= n; i++)
            for (int j = 0; j <= 1011; j++)
                g[i][j] = false;
        return dp(0, 0);
    }
};
