constexpr int mod = 1E9 + 7;

class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        const int n = nums.size();

        vector dp(n, vector<int>(51, -1));
        auto dfs = [&](auto &&self, int idx, int x) -> int {
            if (idx == n) {
                return 1;
            }
            if (~dp[idx][x]) {
                return dp[idx][x];
            }
            int ans = 0;
            for (int i = 0; i <= nums[idx]; ++i) {
                int j = nums[idx] - i;
                if (x > i || (idx == 0 ? 50 : nums[idx - 1] - x) < j) {
                    continue;
                }
                ans = (ans + self(self, idx + 1, i)) % mod;
            }
            return dp[idx][x] = ans;
        };
        int ans = dfs(dfs, 0, 0);
        return ans;
    }
};
