class Solution {
public:
    typedef long long ll;

    ll dp[2020][53];
    int n;
    ll mod = 1e9+7;

    ll rec(int i, ll prev, vector<int>& nums){
        ll p1 = prev;
        ll p2 = (i!=0)?(nums[i-1] - p1):nums[0];
        if(p2<0){
            return 0LL;
        }
        if(i==n){
            return dp[i][prev]=1;
        }
        if(dp[i][prev]!=-1){
            return dp[i][prev];
        }

        ll ans=0;

        for(int c1=0;c1<=nums[i];c1++){
            int c2 = nums[i]-c1;
            if(c1>=p1 && c2<=p2){
                ans = (ans + rec(i+1,c1,nums))%mod;
            }
        }

        return dp[i][prev]=ans;

    }

    int countOfPairs(vector<int>& nums) {
        memset(dp,-1,sizeof(dp));
        n=nums.size();
        return rec(0,0,nums);
    }
};
