class Solution {
public:
    int mod=1e9+7;
    int solve(int index,vector<int>&nums,int p1,int p2,vector<vector<vector<int>>>&dp){
        if(index==nums.size()){
            return 1;
        }
        if(dp[index][p1][p2]!=-1)return dp[index][p1][p2];
        int ans=0;
        for(int i=0;i<=nums[index];i++){
            if(p1<i){
                break;
            }
            else{
                if(p2>nums[index]-i){
                    break;
                }
                else{
                    ans=(ans+solve(index+1,nums,i,nums[index]-i,dp))%mod;
                }
            }
        }
        return dp[index][p1][p2]= ans;
    }
    int countOfPairs(vector<int>& nums) {
        int ans=0;
        vector<vector<vector<int>>>dp(nums.size(),vector<vector<int>>(51,vector<int>(51,-1)));
        for(int i=0;i<=nums[0];i++){
            ans=(ans+solve(1,nums,i,nums[0]-i,dp))%mod;
        }
        return ans;
    }
};
