class Solution {
public:
    int mod=1e9+7;
    int recursion(vector<int> &nums,int i,int arr1Prev,int arr2Prev,vector<vector<vector<int>>> &dp){
        if(i>=nums.size()){
            return 1;
        }
        if(dp[i][arr1Prev][arr2Prev+1]!=-1){
            return dp[i][arr1Prev][arr2Prev+1];
        }
        int t1=arr1Prev, t2=nums[i]-arr1Prev;
        if(t2<0)
            return 0;
        int ans=0;
        for(int t1=arr1Prev;t1<=nums[i];++t1){
            t2=nums[i]-t1;
            if(arr2Prev==-1 || t2<=arr2Prev)
                ans=(ans%mod+recursion(nums,i+1,t1,t2,dp)%mod)%mod;
        }
        return dp[i][arr1Prev][arr2Prev+1]=ans;
    }
    int countOfPairs(vector<int>& nums) {
        int n=nums.size();
        vector<vector<vector<int>>> dp(n,vector<vector<int>>(51,vector<int>(53,-1)));
        return recursion(nums,0,0,-1,dp);
    }
};
