class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        MOD = 10**9 + 7

        memo = {}

        def dfs(index, prev_arr1):
            if index == len(nums):
                return 1

            if (index, prev_arr1) in memo:
                return memo[(index, prev_arr1)]

            total_ways = 0
            for arr1 in range(prev_arr1, nums[index] + 1):
                arr2 = nums[index] - arr1
                if arr2 >= 0:
                    if index == 0 or arr2 <= nums[index - 1] - prev_arr1:
                        total_ways = (total_ways + dfs(index + 1, arr1)) % MOD

            memo[(index, prev_arr1)] = total_ways
            return total_ways

        return dfs(0, 0)
