class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        int n=nums.size();
        vector<vector<vector<int>>>v(n,vector<vector<int>>(51,vector<int>(52,-1)));
        return solver(nums,0,0,51,v);
    }
    int solver(vector<int>& nums,int i,int a,int b,vector<vector<vector<int>>>&v){
        if(i==nums.size())return 1;
        if(v[i][a][b]!=-1)return v[i][a][b];
        int count=0;
        int mod=1e9+7;
        for(int j=a;j<=nums[i];j++){
            if(nums[i]-j<=b)count=(count+solver(nums,i+1,j,nums[i]-j,v) )%mod;
        }
        return v[i][a][b]= count;
    }
};
