class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        int M=1e9+7;
        int n=nums.size();
        vector<vector<int>>dp(n,vector<int>(51,0));


        for(int i=0;i<=nums[0];i++){
            dp[0][i]=1;
        }

        for(int i=1;i<n;i++){


            for(int j=0;j<=nums[i];j++){

                int count=0;

                for(int k=0;k<=j;k++){
                if(nums[i-1]-k>= nums[i]-j )count=(count+0LL+dp[i-1][k])%M;
                }

                dp[i][j]=count;
            }

        }

        int ans=0;

        for(int i=0;i<51;i++){
            ans=(ans+0LL+dp[n-1][i])%M;
        }

        return ans;

    }
};
