#define ll long long
#define vi vector<ll>
class Solution
{
public:
    const ll MOD = 1e9 + 7;
    const int N = 51;
    ll f(int posn, vector<int> &v, vector<vector<vi>> &dp, int v1, int v2, ll ans)
    {
        // Base Case
        if (posn >= v.size())
        {
            return 1;
        }
        // Memo Check
        if (dp[posn][v1][v2] != -1)
        {
            return dp[posn][v1][v2];
        }

        for (int i = v1; i <= v[posn]; i++)
        {
            int sum = 0;
            for (int j = v2; j >= 0; j--)
            {
                sum = i + j;
                if (v[posn] == sum)
                {
                    ans += f(posn + 1, v, dp, i, j, 0);
                    ans %= MOD;
                }
            }
        }

        return dp[posn][v1][v2] = ans;
    }

    int countOfPairs(vector<int> &v)
    {
        int n = v.size();
        vector<vector<vi>> dp(n, vector<vi>(N, vi(N, -1)));
        int res = f(0, v, dp, 0, v[0], 0);
        return res;
    }
};
