class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        MOD = 10**9 + 7
        n = len(nums)
        ma = max(nums)
        dp = [[0] * (ma + 1) for _ in range(n)]
        for j in range(nums[0] + 1):
            dp[0][j] = 1
        for i in range(1, n):
            for j in range(nums[i] + 1):
                a1 = j
                a2 = nums[i] - j
                for k in range(0, ma + 1):    
                    if nums[i] - j <= nums[i - 1] - k and j >= k:
                        dp[i][j] += dp[i - 1][k]
        result = sum(dp[n-1][j] for j in range(nums[-1] + 1)) % MOD
        return result
