class Solution {
       Long dp[][][];
       int mod = 1000_000_007;
       public int countOfPairs(int[] nums) {
           int n = nums.length;
           dp = new Long[n][52][52];
           return  (int)helper(nums, 0, 0, 51);
       }

       private  long helper(int nums[], int cur, int pre1, int pre2){
           if(cur >= nums.length)return 1;

           if(dp[cur][pre1][pre2]!=null){
               return dp[cur][pre1][pre2];
           }

           long ans = 0;
           for(int i=0;i<=nums[cur];i++){
                int next1 = i;
               int next2 = nums[cur]-i;

               if(pre1 <= next1 && pre2 >= next2){
                   ans = (ans + helper(nums, cur+1, next1, next2)%mod)%mod;
               }
           }
           return dp[cur][pre1][pre2] = ans;
       }

   }
