class Solution {
public:
    int rec(vector<int> &v, int n, int i, int start, int end, vector<vector<vector<int> > > &dp){
        if(i >= n) return 1;
        if(dp[i][start][end] != -1) return dp[i][start][end];
        int j = v[i], k = 0, ans=0;
        while(j>=start && k<=end){
            ans = (ans + rec(v, n, i+1, j, k, dp)) % 1000000007;
            j--; k++;
        }
        return dp[i][start][end] = ans;
    }

    int countOfPairs(vector<int>& v) {
        int n = v.size();
        vector<vector<vector<int> > > dp(n, vector<vector<int> > (52, vector<int>(52, -1)));
        int ans = rec(v, n, 0, 0, 51, dp);
        return ans;
    }
};
