class Solution {
    public int countOfPairs(int[] nums) {
        int n=nums.length;
        int dp[][][]=new int[n][51][51];
        int mod=1000000007;
        for(int i=0;i<n;i++)
        {
            int val=nums[i];
            for(int j=0;j<=val;j++)
            {
                int a=j;
                int b=val-j;
                if(i==0)
                {
                    dp[i][a][b]=1;
                }
                else
                {
                    for(int k=0;k<=a;k++)
                    {
                        int pa=k;
                        int pb=nums[i-1]-k;
                        if(b<=pb)
                        {
                            dp[i][a][b]+=dp[i-1][pa][pb];
                            dp[i][a][b]%=mod;
                        }
                    }
                }

            }
        }
        int ans=0;
        for(int i=0;i<=50;i++)
        {
            int a=i;
            int b=nums[n-1]-a;
            if(b>=0)
            {
                ans=ans+dp[n-1][a][b];
                ans%=mod;
            }
        }
        return ans;
    }
}
