class Solution {
public:
    int n  ;
    int MOD = 1000000007 ;
    vector<int>arr1 , arr2 ;
    int solve(int i , vector<int>&nums ,  vector<vector<vector<int>>>&dp){
        if(i == n)return 1 ;
        int lst1 = (i > 0)?arr1[i - 1]:0 , lst2 = (i > 0)?arr2[i - 1]:51 ;
        if(dp[i][lst1][lst2] != -1)return dp[i][lst1][lst2] ;
        int ans = 0 ;
        for(int j = lst1 ; j <= nums[i] ; j++){
            if(lst2 >= (nums[i] - j)){
                arr1.push_back(j) ;
                arr2.push_back(nums[i] - j) ;
                ans = (ans + solve(i + 1 , nums , dp))%MOD ;
                arr1.pop_back() ;
                arr2.pop_back() ;
            }
        }
        return dp[i][lst1][lst2] = ans%MOD ;
    }
    int countOfPairs(vector<int>& nums) {
        n = nums.size() ;
        vector<vector<vector<int>>>dp(n + 1 , vector<vector<int>>(52 , vector<int>(52 , -1))) ;
        return solve(0 , nums , dp) ;
    }
};
