class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        n, max_num = len(nums), max(nums)
        dp = [[[0 for _ in range(max_num + 1)] for _ in range(max_num + 1)] for _ in range(n)]

        for j in range(nums[0] + 1):
            dp[0][j][nums[0] - j] = 1

        # print(dp)

        for i in range(1, n):
            for j in range(nums[i] + 1):
                for jj in range(j + 1):
                    if nums[i] - j <= nums[i - 1] - jj:
                        dp[i][j][nums[i] - j] += dp[i - 1][jj][nums[i - 1] - jj]
                        dp[i][j][nums[i] - j] %= 10 ** 9 + 7

        # print(dp)

        ans = 0
        for j in range(nums[-1] + 1):
            ans += dp[n - 1][j][nums[-1] - j] % (10 ** 9 + 7)

        return ans % (10 ** 9 + 7)
