class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
      MOD = 10**9 + 7
      n = len(nums)
      @cache
      def helper(i, p1, p2):
        # print(i, p1, p2)
        cnt = 0
        if i == n-1:
          for p11 in range(nums[i]+1):
            if p1<=p11 and p2>=nums[i]-p11:
              cnt+=1
          # print(cnt)
          return cnt

        for p11 in range(nums[i]+1):
          if p1<=p11 and p2>=nums[i]-p11:
            cnt+=helper(i+1, p11, nums[i]-p11)
        return cnt
      return helper(0, 0, 99999) % MOD
