class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        MOD = int(1e9) + 7
        pairs = [[] for _ in range(len(nums))]
        for i in range(len(nums)):
            for j in range(nums[i] + 1):
                pairs[i].append([j, nums[i] - j, 0])
        pairs[0] = [[x[0], x[1], 1] for x in pairs[0]]

        for i in range(1, len(pairs)):
            for j in range(len(pairs[i - 1])):
                for k in range(len(pairs[i])):
                    if pairs[i][k][0] >= pairs[i - 1][j][0] and pairs[i][k][1] <= pairs[i - 1][j][1]:
                        pairs[i][k][2] = (pairs[i][k][2] + pairs[i - 1][j][2]) % MOD

        return (sum([x[2] for x in pairs[-1]])) % MOD
